<script src="https://cdn.jsdelivr.net/npm/sweetalert2@11"></script>


<script>
    document.addEventListener('DOMContentLoaded', function() {
        // Attach click event listener to elements with class EmailSent
        document.querySelectorAll('.EmailSent').forEach(function(element) {
            element.addEventListener('click', function() {
                const courseUrl = element.getAttribute('data-url');
                Swal.fire({
                    title: 'Your Request Is On Its Way!',
                    html: 'Thank you for your interest! We have just dispatched a special email to your inbox. This exclusive message contains a personal link to access the course, EPN had been notified  about your interest in the course and will get back to you via email.<br><br>Please check your email and follow the conversation to provide the required registration details. We’re thrilled to have you embark on this journey with us—where learning and empowerment meet. Your gateway to a world of knowledge is just a few clicks away!',
                    icon: 'success',
                    confirmButtonText: 'View Course on EPN',
                    footer: '<a href="mailto:support@example.com">Need help? Contact us!</a>'
                }).then((result) => {
                    if (result.isConfirmed) {
                        // Navigate to the URL when the user clicks the button
                        window.open(courseUrl, '_blank');
                    }
                });
            });
        });
    });
</script>
